import { useEffect } from "react";
import { Toaster as Sonner, toast } from "sonner";
import {TooltipProvider} from "@/components/ui/tooltip"; // Assure-toi que `toast` est importé
import { Toaster } from "./components/ui/sonner";
import {BrowserRouter, Routes} from "react-router-dom";
import {Route} from "lucide-react";
import Index from "@/pages/Index";
import NotFound from "@/pages/NotFound";

const App = () => {
    useEffect(() => {
        const handleServerDown = () => {
            toast.error("Le serveur est actuellement indisponible.");
        };

        window.addEventListener("server-down", handleServerDown);
        return () => window.removeEventListener("server-down", handleServerDown);
    }, []);

    return (
        <TooltipProvider>
            <Toaster />
            <Sonner />
            <BrowserRouter>
                <Routes>
                    <Route path="/" element={<Index />} />
                    <Route path="*" element={<NotFound />} />
                </Routes>
            </BrowserRouter>
        </TooltipProvider>
    );
};

export default App;
