import axios from 'axios';

const API_URL = 'http://localhost:3000/tasks';

export const fetchTasks = () => axios.get(API_URL).then(res => res.data);

export const createTask = (data: { title: string; description: string }) =>
  axios.post(API_URL, data).then(res => res.data);

export const deleteTask = (id: number) =>
  axios.delete(`${API_URL}/${id}`);

export const updateStatus = (id: number, status: 'pending' | 'done') =>
  axios.patch(`${API_URL}/${id}`, { status });
